import React, { Component } from "react";
import { connect } from 'react-redux';
import { bindActionCreators } from "redux";
import { authEndpoint, clientId, redirectUri, scopes } from "../config";
import { hasLoaded } from "../actions/uiActions";
import '../styles/Group.css';
import topLeft from '../topLeft.png';
import topRight from '../topRight.png';
import Player from './Player.js';
import axios from 'axios';
import SpotifyPlayer from 'react-spotify-web-playback';
import $ from "jquery";

class Group extends Component{

    state = {
      data: [],
      intervalIsSet:false,
      ownerToken: "",
      item: {
        album: {
          images: [{ url: "" }]
        },
        name: "",
        artists: [{ name: "" }],
        duration_ms: 0
      },
      is_playing: "Paused",
      progress_ms: 0
    }

    componentDidMount() { 
      this.getDataFromDb();   
        if (!this.state.intervalIsSet) {
          let interval = setInterval(this.getDataFromDb, 1000);
          this.setState({ intervalIsSet: interval });
        }
        if(this.state.data.length>0){
          let curGroup = this.state.data.find(element => element.group === this.props.group);
          this.setState({ownerToken:curGroup.ownerToken});
          this.getCurrentlyPlaying(this.state.ownerToken);
        }
        window.onbeforeunload = function() {
          this.onUnload();
          return "";
        }.bind(this);
        this.props.hasLoaded(true);
      }

    componentWillUnmount() {
        if (this.state.intervalIsSet) {
          clearInterval(this.state.intervalIsSet);
          this.setState({ intervalIsSet: null });
        }
        this.props.hasLoaded(false);
      }

      getDataFromDb = () => {
        fetch('http://localhost:3001/api/getData')
          .then((data) => data.json())
          .then((res) => this.setState({ data: res.data }));
      };

      displayOwner(){
        let curGroup = this.state.data.find(element => element.group === this.props.group);
        return curGroup.ownerName;
      }

      getCurrentlyPlaying(token) {

        $.ajax({
          url: "https://api.spotify.com/v1/me/player",
          type: "GET",
          beforeSend: xhr => {
            xhr.setRequestHeader("Authorization", "Bearer " + token);
          },
          success: data => {
            if(typeof data !== 'undefined'){
              this.setState({
                item: data.item,
                is_playing: data.is_playing,
                progress_ms: data.progress_ms
              });
            }
            else if(this.props.isOwner){
              alert("No song currently playing, start a song and refresh");
            }
            else{
              alert("Group owner is no longer playing music, refresh to check again.");
            }
          }
        });
      }

      /*
      syncWithOwner(token){

        $.ajax({
          url: "https://api.spotify.com/v1/me/player/play",
          type: "PUT",
          data: '{"uris": ['+ this.state.data.item + '], "position_ms": [' + this.state.data.progress_ms + ']}',
          beforeSend: xhr => {
            xhr.setRequestHeader("Authorization", "Bearer " + token);
          },
          success: (
            console.log("synced")
          )
        });
      }
      */

      render() {

        if(!this.props.isLoaded){
          return(
          <div className="Group">
            <header className="Group-header">
            <h1 className="Group-title">Listen Together</h1>
                <img src={topLeft} className="Group-logoLeft" alt="logo"/>
                <img src={topRight} className="Group-logoRight" alt="logo"/>
            <div class="loader"></div>
            </header>
          </div>
          )
        }
        if(this.props.isLoaded && this.state.data.length == 0){
          return(
          <div className="Group">
            <header className="Group-header">
            <h1 className="Group-title">Listen Together</h1>
            <img src={topLeft} className="Group-logoLeft" alt="logo"/>
            <img src={topRight} className="Group-logoRight" alt="logo"/>

            <p className="Group-curGroup">Loading...</p>
            <div class="loader"></div>

            <p className="Home-p">
            Created By Kyle Litton. <a className="Home-credit" href='https://github.com/jkl1999/ListenTogether'>View Source</a>
            </p>
            </header>

          </div>
          )
        }
        else{
          if(this.props.isLoaded && !this.props.username){
            return(
              <div className="Group">
                <header className="Group-header">
                <h1 className="Group-title">Listen Together</h1>
                    <img src={topLeft} className="Group-logoLeft" alt="logo"/>
                    <img src={topRight} className="Group-logoRight" alt="logo"/>
                <h3 className="Group-loadError">Something Went Wrong</h3>
                <a
              className="Group-tryAgain"
              href={`${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join(
                "%20"
              )}&response_type=token&show_dialog=true`}
            >
              Try Again
            </a>
                </header>
              </div>
              )
          }
          if(!this.props.isOwner){
            return (
              <div className="Group">
                <header className="Group-header">
                <h1 className="Group-title">Listen Together</h1>
                <img src={topLeft} className="Group-logoLeft" alt="logo"/>
                <img src={topRight} className="Group-logoRight" alt="logo"/>

                <p className="Group-curGroup">{'Current Group: ' + this.props.group}</p>
                <p className="Group-groupOwner">{this.displayOwner() + ' Is Playing The Music'}</p>
                {this.getCurrentlyPlaying(this.props.ownerToken)}
                <div className='Group-player'>
                    <Player
                      item={this.state.item}
                      is_playing={this.state.is_playing}
                      progress_ms={this.progress_ms}
                      />
                      <br></br>
                      <br></br>
                      <SpotifyPlayer
                      styles={{
                        loaderColor: '#1db954',
                        sliderColor: '#1db954',
                      }}
                      token={this.props.token}
                      uris={this.state.data.item}
                      autoPlay="true"
                      showSaveIcon="true"
                      />;
                  </div>
                <p className="Home-p">
                Created By Kyle Litton. <a className="Home-credit" href='https://github.com/jkl1999/ListenTogether'>View Source</a>
                </p>
                </header>

              </div>
            );
          }
          if(this.props.isOwner){
            return (
              <div className="Group">
                <header className="Group-header">
                <h1 className="Group-title">Listen Together</h1>
                <img src={topLeft} className="Group-logoLeft" alt="logo"/>
                <img src={topRight} className="Group-logoRight" alt="logo"/>

                <p className="Group-curGroup">{'Current Group: ' + this.props.group}</p>
                <p className="Group-groupOwner">{'You Are Playing The Music'}</p>  
                {this.getCurrentlyPlaying(this.props.ownerToken)}           
                  <div className='Group-player'>
                    <Player
                      item={this.state.item}
                      is_playing={this.state.is_playing}
                      progress_ms={this.progress_ms}
                      />
                      <br></br>
                      <br></br>
                      <SpotifyPlayer
                      styles={{
                        loaderColor: '#1db954',
                        sliderColor: '#1db954',
                      }}
                      token={this.props.token}
                      showSaveIcon="true"
                      play="true"
                      />;
                  </div>



                <p className="Home-p">
                Created By Kyle Litton. <a className="Home-credit" href='https://github.com/jkl1999/ListenTogether'>View Source</a>
                </p>
                </header>

              </div>
            );
          }        
        }
      }


}

const mapDispatchToProps = dispatch => {
  return bindActionCreators(
    {
      hasLoaded
    },
    dispatch
  );
};

const mapStateToProps = state => {
    return {
      isLoaded: state.uiReducer.isLoaded,
