export default (state = {}, action) =>{

    switch(action.type){

        case 'ADD_TOKEN':
            return{
                ...state,
                token: action.token
            }
        case 'SET_USERNAME':
            return{
                ...state,
                username: action.username
            }
        case 'SET_GROUP':
            return{
                ...state,
                group: action.group
            }
        case 'SET_ISOWNER':
            return{
                ...state,
                isOwner: action.val
            }       
        default:
            return state

    }

}
