import React from "react";
import "../styles/Player.css";

const Player = props => {
  const backgroundStyles = {
    backgroundImage:`url(${
      props.item.album.images[0].url
    })`,
  };

  if(!props.item.album.images[0].url==""){
    return (
      <div className="App">
        <div className="main-wrapper">
        <p className="Group-groupOwner">{props.isOwner ? 'You Are Playing The Music' : props.owner + ' is playin the music'}</p>  
          <div className="now-playing__img">         
            <img src={props.item.album.images[0].url} />
          </div>
          <div className="background" style={backgroundStyles} />{" "}
        </div>
      </div>
    );
  }
  else{
    return (
      <div className="App">
        <div className="main-wrapper">
            <p className="instructions">Select the Device Button, Then select "Listen Together" to play music.</p>
          <div className="background" style={backgroundStyles} />{" "}
        </div>
      </div>
    );
  }
}



export default Player;
