import React, { Component } from "react";
import { connect } from 'react-redux';
import { authEndpoint, clientId, redirectUri, scopes } from "../config";
import { addToken, setUsername } from "../actions/userAction"
import '../App.css';
var Spotify = require('spotify-web-api-js');
var s = new Spotify();

const mapDispatchToProps = dispatch => ({
    addToken: (token) => dispatch(addToken(token)),
    setUsername: (name) => dispatch(setGroup(name)),
})

const mapStateToProps = state => {
    return {
      ...state
    };
  };


class Home extends Component {

  componentDidMount() {
    let hashParams = {};
    let e,
      r = /([^&;=]+)=?([^&;]*)/g,
      q = window.location.hash.substring(1);
    while ((e = r.exec(q))) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
    }

    if (hashParams.access_token) {
      this.props.addToken(hashParams.access_token);
      s.setAccessToken(this.props.token);
    } 
  }

  getUsername(){
    s.getMe()
    .then((response) => {
        let name = response.item.display_name;
        this.props.setUsername(name);
    })
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          {!this.props.token && (
            <a
              className="btn btn--loginApp-link"
              href={`${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join(
                "%20"
              )}&response_type=token&show_dialog=true`}
            >
              Login to Spotify
            </a>
          )}
          {this.props.token && (
            <div>
                <button>Create a Group</button>
                <p>Create a new group that friends can join.</p>
                <button>Join a Group</button>
                <p>Join a group that already exists.</p>
            </div>
          )}
        </header>
      </div>
    );
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(Home);
