import React, { Component } from "react";
import { connect } from 'react-redux';
import { bindActionCreators } from "redux";
import { setGroup } from "../actions/userAction";
import '../styles/Group.css';
import logo from '../logo.png'
import axios from 'axios';

class CreateGroup extends Component{

    state = {
      data: [],
      intervalIsSet:false,
    }

    componentDidMount() {    
        this.getDataFromDb();
        if (!this.state.intervalIsSet) {
          let interval = setInterval(this.getDataFromDb, 1000);
          this.setState({ intervalIsSet: interval });
        }

      }

    componentWillUnmount() {
        if (this.state.intervalIsSet) {
          clearInterval(this.state.intervalIsSet);
          this.setState({ intervalIsSet: null });
        }
      }

      getDataFromDb = () => {
        fetch('http://localhost:3001/api/getData')
          .then((data) => data.json())
          .then((res) => this.setState({ data: res.data }));
      };

      render() {
        return (
          <div className="Group">
            <header className="Group-header">
            <img src={logo} className="Group-logo" alt="logo"/>

            <p className="Group-p">{this.props.group}</p>


             <p className="Group-p">
            Created By Kyle Litton. <a className="Home-credit" href='https://github.com/jkl1999/ListenTogether'>View Source</a>
            </p>
            </header>

          </div>
        );
      }


}

const mapDispatchToProps = dispatch => {
  return bindActionCreators(
    {
      setGroup
    },
    dispatch
  );
};

const mapStateToProps = state => {
    return {
      group: state.userReducer.group,
      token: state.userReducer.token,
      username: state.userReducer.username,
      isOwner: state.userReducer.isOwner
    };
  };

export default connect(mapStateToProps, mapDispatchToProps)(CreateGroup);
