export default (state = {}, action) =>{

    switch(action.type){

        case 'ADD_TOKEN':
            return{
                ...state,
                Token: action.payload
            }
        case 'SET_USERNAME':
            return{
                ...state,
                Username: action.payload
            }
        case 'SET_GROUP':
            return{
                ...state,
                Group: action.payload
            }
        case 'SET_ISOWNER':
            return{
                ...state,
                isOwner: action.payload
            }       
        default:
            return state

    }

}
